import React, { useState } from 'react';

function App() {
  const [mensaje, setMensaje] = useState('');
  const [conversacion, setConversacion] = useState([]);
  const [cargando, setCargando] = useState(false);

  const backendUrl = process.env.REACT_APP_BACKEND_URL || 'http://localhost:3001';

  const enviarMensaje = async (e) => {
    e.preventDefault();
    if (!mensaje.trim()) return;

    setConversacion([...conversacion, { remitente: 'usuario', texto: mensaje }]);
    setCargando(true);

    try {
      const res = await fetch(`${backendUrl}/api/chat`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ mensaje })
      });
      const data = await res.json();
      setConversacion((prev) => [
        ...prev,
        { remitente: 'bot', texto: data.respuesta }
      ]);
    } catch (error) {
      setConversacion((prev) => [
        ...prev,
        { remitente: 'bot', texto: 'Error al conectar con el backend.' }
      ]);
    } finally {
      setMensaje('');
      setCargando(false);
    }
  };

  return (
    <div style={{ maxWidth: 500, margin: '40px auto', fontFamily: 'sans-serif' }}>
      <h2>FisioVanguardia Chatbot</h2>
      <div style={{ border: '1px solid #ccc', padding: 16, minHeight: 200, marginBottom: 16 }}>
        {conversacion.map((msg, idx) => (
          <div key={idx} style={{ textAlign: msg.remitente === 'usuario' ? 'right' : 'left' }}>
            <b>{msg.remitente === 'usuario' ? 'Tú' : 'Bot'}:</b> {msg.texto}
          </div>
        ))}
        {cargando && <div>El bot está escribiendo...</div>}
      </div>
      <form onSubmit={enviarMensaje}>
        <input
          type="text"
          value={mensaje}
          onChange={e => setMensaje(e.target.value)}
          placeholder="Escribe tu mensaje..."
          style={{ width: '80%', padding: 8 }}
          disabled={cargando}
        />
        <button type="submit" disabled={cargando || !mensaje.trim()} style={{ padding: 8 }}>
          Enviar
        </button>
      </form>
    </div>
  );
}

export default App;
